window.onload=function(){let e=document.querySelector(".lang-dropdown"),t=getParameterByName("lang")||"en";localStorage.setItem("lang",t);let n=localStorage.getItem("lang"),a=e.querySelectorAll(".dropdown-item");for(i=0;i<a.length;i++)a[i].classList.remove="active";a[findLangIndex(n)].classList.add="active",a.forEach((e=>{e.addEventListener("click",(function(){let e=this.getAttribute("data-lang");localStorage.setItem("lang",e),MultiLanguage()}))}));let l=document.querySelector(".btn-switch-tab"),o=document.querySelector(".unity-menu");l.addEventListener("click",(function(){o.classList.toggle("show")}))};