function getParameterByName(e,t=window.location.href){e=e.replace(/[\[\]]/g,"\\$&");var a=new RegExp("[?&]"+e+"(=([^&#]*)|&|#|$)").exec(t);return a?a[2]?decodeURIComponent(a[2].replace(/\+/g," ")):"":null}function findLangIndex(e){switch(e){case"es":e=1;break;case"de":e=2;break;case"it":e=3;break;case"fr":e=4;break;default:e=0}return e}function MultiLanguage(){console.log((localStorage.getItem("lang"),localStorage.getItem("lang"))),unityInstance.SendMessage("Language_Manager","SendToUnity",localStorage.getItem("lang")?localStorage.getItem("lang"):"en"),unityInstance.SendMessage("Language_Manager","loadLanguage")}window.onload=function(){let e=document.querySelector(".lang-dropdown"),t=getParameterByName("lang")||"en";localStorage.setItem("lang",t);let a=localStorage.getItem("lang"),n=e.querySelectorAll(".dropdown-item");for(i=0;i<n.length;i++)n[i].classList.remove="active";n[findLangIndex(a)].classList.add="active",n.forEach((e=>{e.addEventListener("click",(function(){let e=this.getAttribute("data-lang");localStorage.setItem("lang",e),MultiLanguage()}))}));let l=document.querySelector(".btn-switch-tab"),o=document.querySelector(".unity-menu");l.addEventListener("click",(function(){o.classList.toggle("show")}))};