$((function(){gsap.timeline().to(".unity-map-overlay",{opacity:0,display:"none",duration:.8,ease:"power2.out"}).from(".unity-map-slogan ,.unity-map--logo",{opacity:0,x:-90,duration:1,ease:"power2.out"},"-=.8").from(".unity-map--screen",{opacity:0,marginTop:100,duration:.8,stagger:.3,ease:"power2.out"},"-=.5").from(".unity-map-bg .base-bg",{opacity:0,duration:1,scale:1.01,ease:"power2.out"}).to(".unity-map-hover",{display:"block"});let e=$(".unity-map--screen");$(".unity-map-hover-screen").on("mouseover",(function(){let o=$(this).index();e.eq(o).addClass("active")})).on("mouseleave",(function(){let o=$(this).index();e.eq(o).removeClass("active")}))}));